import React, { useState } from "react";
import "../styles/editor.scss";
import "../styles/App.css";
import "@fontsource/press-start-2p";

export default function Editor() {
    const [panelWidth, setPanelWidth] = useState(16);
    const [panelHeight, setPanelHeight] = useState(14);
    const [hideOptions, setHideOptions] = useState(false);
    const [hideDrawingPanel, setHideDrawingPanel] = useState(true);
    const [buttonText, setButtonText] = useState("draw");
    const [selectedColor, setColor] = useState("#yeahha");

    return (
    <div id="editor">
        <h1>ReactJS Pixel Art</h1>
        <h2>Enter Dimensions</h2>
        <div id="options"></div>
        <div className="option">
            <input 
                type="number" 
                className="panelInput" 
                defaultValue={panelWidth} 
                // onChange={}
            ></input>
            <span> Width</span>
        </div>
        <div className="option">
            <input 
                type="number" 
                className="panelInput" 
                defaultValue={panelHeight} 
                // onChange={}
            ></input>
            <span>Height</span>
        </div>
        
        <button className="button">draw</button>
    </div>
    );

}
